import { createContext, ReactNode, useContext, useState } from 'react';
import { toast } from 'react-toastify';
import { api } from '../services/api';
import { Product, Stock } from '../types';

interface CartProviderProps {
  children: ReactNode;
}

interface UpdateProductAmount {
  productId: number;
  amount: number;
}

interface CartContextData {
  cart: Product[];
  addProduct: (productId: number) => Promise<void>;
  removeProduct: (productId: number) => void;
  updateProductAmount: ({ productId, amount }: UpdateProductAmount) => void;
}

const CartContext = createContext<CartContextData>({} as CartContextData);

export function CartProvider({ children }: CartProviderProps): JSX.Element {
  const [cart, setCart] = useState<Product[]>(() => {
    // const storagedCart = Buscar dados do localStorage

    // if (storagedCart) {
    //   return JSON.parse(storagedCart);
    // }

    return [];
  });

  const addProduct = async (productId: number) => {

    try {
      // TODO
      const response = await api.get(`/products/${productId}`);

      const productInCart = cart.find(item => item.id === productId);

      if(productInCart) {
        const amount = productInCart.amount + 1;
        updateProductAmount({productId, amount});
      } else {
        response.data.amount = 1;
        setCart(oldState => [...oldState, response.data])
      }
    } catch {
      // TODO
    }
  };

  const removeProduct = (productId: number) => {
    try {
      // TODO
      const findProductInCart = cart.find(item => item.id === productId);

      if(findProductInCart) {
        const removeProducInCart = cart.filter(item => item.id !== productId);

        setCart(removeProducInCart);
      }
    } catch {
      // TODO
    }
  };

  const updateProductAmount = async ({
    productId,
    amount,
  }: UpdateProductAmount) => {
    try {
      // TODO
      const response = await api.get(`/stock`);
      const stock: Stock[] = response.data;
      const stockByProduct = stock.find(item => item.id === productId);

      if(stockByProduct?.id) {
        const updateValited = stockByProduct.amount - amount < 0 ? false : true;
        if (updateValited) {
          const updatedCart = cart.map(item => item.id === productId ? {
            ...item, amount: amount
          } : item);

          setCart(updatedCart);
        }
      }
    } catch {
      // TODO
    }
  };

  return (
    <CartContext.Provider
      value={{ cart, addProduct, removeProduct, updateProductAmount }}
    >
      {children}
    </CartContext.Provider>
  );
}

export function useCart(): CartContextData {
  const context = useContext(CartContext);

  return context;
}
